const SideBar = () => {
  const navigate = useNavigate();
  const [selectedItem, setSelected] = useState(0);

  const HandleClickSelectedItem = (item) => {
    setSelected(item.id);

    navigate(item.link);
  };

  return (
    <div className=" h-screen   bg-SideBar flex flex-col items-center cursor-pointer ">
      <UserDetail>
        <UserIcon>
          <Image src={icon_user} />
        </UserIcon>
        <Details>
          <Name>Buzz Coworking</Name>
          <Address>123 Main Street</Address>
        </Details>
        <SelectorIcon>
          <Image src={icon_selector} />
        </SelectorIcon>
      </UserDetail>
      <MenuItemsListContainer>
        {MenuItems.map((items) => {
          return (
            <ItemSelection
              Itemid={items.id}
              selectedItem={selectedItem}
              onClick={() => HandleClickSelectedItem(items)}
            >
              <StyledSVG
                xmlns={"http://www.w3.org/200/svg"}
                strokeColor={selectedItem === items.id ? "white" : "grey"}
                strokeWidth={selectedItem === items.id ? 2 : 1}
                fill="none"
                height={30}
                width={30}
              >
                <path d={items.path} />
                <path d={items?.path2} />
              </StyledSVG>
              {items.title}
            </ItemSelection>
          );
        })}
      </MenuItemsListContainer>
    </div>
  );
};

export default SideBar;
